s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:13:"Brad Williams";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1065:"<p><a href="http://webdevstudios.com/wp-content/uploads/2011/11/wordcamp-las-vegas.png" rel="shadowbox[sbpost-6317];player=img;"><img src="http://webdevstudios.com/wp-content/uploads/2011/11/wordcamp-las-vegas.png" alt="" title="wordcamp-las-vegas" width="125" height="125" class="alignleft size-full wp-image-6298" /></a>WebDevStudios is heading west to attend <a href="http://2011.vegas.wordcamp.org/" target="_blank">WordCamp Las Vegas</a> on December 17th, 2011!  The WDS founders, Brad and Brian, will be attending what looks to be an awesome WordCamp.  We&#8217;re also very proud to be a <a href="http://2011.vegas.wordcamp.org/sponsor/webdevstudios/" target="_blank">Diamond Sponsor</a> of the event.</p>\n<p>If you are attending the event make sure you track <a href="http://webdevstudios.com/team/brad-williams/" title="Brad Williams WordPress">Brad</a> and <a href="http://webdevstudios.com/team/brian-messenlehner/" title="Brian Messenlehner">Brian</a> down.  They are always looking to talk WordPress and might even have a few goodies to give away!</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:82:"http://webdevstudios.com/2011/11/30/wds-is-attending-wordcamp-las-vegas-2011/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:4;a:6:{s:4:"data";s:62:"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n		\n		\n\n		\n		\n			\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:32:"Successful WordCamp Philly 2011!";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:68:"http://webdevstudios.com/2011/11/07/successful-wordcamp-philly-2011/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:77:"http://webdevstudios.com/2011/11/07/successful-wordcamp-philly-2011/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 07 Nov 2011 19:49:55 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:8:{i:0;a:5:{s:4:"data";s:7:"General";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:13:"WebDevStudios";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:8:"WordCamp";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:10:"Conference";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:12:"Philadelphia";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:7:"sponsor";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:6;a:5:{s:4:"data";s:9:"WordPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:7;a:5:{s:4:"data";s:16:"WordPress Meetup";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:32:"http://webdevstudios.com/?p=6251";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:396:"It''s no secret that WebDevStudios loves WordCamps! We have sponsored and attended a number of WordCamps all over the country. After attending so many WordCamps we decided to take what we have learned any apply it to planning a WordCamp here in Philadelphia.  <a href="http://webdevstudios.com/2011/11/07/successful-wordcamp-philly-2011/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"April Heline";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:2309:"<p><img class="alignright size-full wp-image-6242" title="wc-philly-sponsor" src="http://webdevstudios.com/wp-content/uploads/2011/10/wc-philly-sponsor.png" alt="" width="150" height="150" />It&#8217;s no secret that WebDevStudios<strong> loves</strong> WordCamps! We have sponsored and attended a number of WordCamps all over the country. We love how WordCamp provides the WordPress community with expert presentations, tons of networking opportunities, hands on training sessions and much more! Events like these only make WordPress and it&#8217;s users stronger and better educated.</p>\n<p>After attending so many WordCamps we decided to take what we have learned any apply it to planning a WordCamp here in Philadelphia. Together with <a href="http://twitter.com/zamoose">Doug Stewart</a>, <a href="http://twitter.com/tangofoxtrot">Reed Gustow</a> and <a href="http://twitter.com/anthonybubel">Anthony Bubel</a>, we put together a massive WordPress-packed weekend! Saturday&#8217;s conference at the <a href="http://community.mis.temple.edu/" target="_blank">Fox School of Business and Management @ Temple University</a> included four expert-packed tracks, a lunchtime academic panel and a hands-on classroom for WordPress training and demos.</p>\n<p>Sunday consisted of a 4-hour developer day at <a href="http://www.philadelphiacfa.org/" target="_blank">Philadelphia Center for Architecture</a> where everyone got together for hands on training on contributing to WordPress core, releasing a plugin and releasing a theme.</p>\n<p>We also had a great after-party Saturday night at <a style="font-size: 17px; line-height: 25px;" href="http://www.nationalmechanics.com/" target="_blank">National Mechanics</a> where everyone got to enjoy some great food, drinks and WordPress chat at a popular local tech meetup restaurant. We even set up a <a href="http://turntable.fm">turntable.fm</a> room where anyone could be the DJ for the night!</p>\n<p>As always, WordCamp would have never been successful without the wonderful organizers, amazing volunteers and all the help we received from a number of people in the Philadelphia area. We can&#8217;t thank everyone enough for helping to hold such a successful WordCamp and (after a short break) are already looking forward to making next year even better!</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:73:"http://webdevstudios.com/2011/11/07/successful-wordcamp-philly-2011/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:5;a:6:{s:4:"data";s:47:"\n		\n		\n		\n		\n		\n				\n		\n		\n\n		\n		\n			\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:35:"This Weekend: WordCamp Boston 2011!";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:70:"http://webdevstudios.com/2011/07/21/this-weekend-wordcamp-boston-2011/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:79:"http://webdevstudios.com/2011/07/21/this-weekend-wordcamp-boston-2011/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 21 Jul 2011 15:34:15 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:3:{i:0;a:5:{s:4:"data";s:8:"WordCamp";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:6:"Boston";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:9:"WordPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:32:"http://webdevstudios.com/?p=6207";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:280:"WordCamp Boston 2011 will be held July 23rd and 24th at the George Sherman Union at Boston University located at at 775 Commonwealth Avenue. <a href="http://webdevstudios.com/2011/07/21/this-weekend-wordcamp-boston-2011/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"April Heline";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1234:"<p>WordCamp Boston 2011 will be held July 23rd and 24th at the George Sherman Union at Boston University located at at <a href="http://www.bu.edu/gsu/location.html">775 Commonwealth Avenue</a>. This is the second annual WordCamp in Boston and after attending last years event we are sure this is a WordCamp that you should not miss!<img class="alignleft size-full wp-image-6169" title="wc-boston-2011-attending" src="http://webdevstudios.com/wp-content/uploads/2011/06/wc-boston-2011-attending.png" alt="" width="150" height="150" /></p>\n<p><a href="http://webdevstudios.com/team/brad-williams/">Brad</a> and <a href="http://webdevstudios.com/team/chris-cochran/">Chris</a> will be attending WordCamp Boston this year and are looking forward to talking shop with other WordPress enthusiasts and all the great presentations Boston has to offer this year.</p>\n<p>For additional information <a title="WordCamp Boston 2011" href="http://2011.boston.wordcamp.org/">visit the website</a>, download the WordCamp Boston <a title="Mobile App" href="http://2011.boston.wordcamp.org/2011/07/21/wordcamp-boston-mobile-app/">mobile app</a>, <a title="WordCampBoston" href="http://twitter.com/#!/WordCampBoston">follow them</a> on Twitter.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:75:"http://webdevstudios.com/2011/07/21/this-weekend-wordcamp-boston-2011/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:6;a:6:{s:4:"data";s:47:"\n		\n		\n		\n		\n		\n				\n		\n		\n\n		\n		\n			\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:35:"Win a Ticket to WordCamp San Diego!";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:71:"http://webdevstudios.com/2011/06/17/win-a-ticket-to-wordcamp-san-diego/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:80:"http://webdevstudios.com/2011/06/17/win-a-ticket-to-wordcamp-san-diego/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 17 Jun 2011 16:37:58 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:3:{i:0;a:5:{s:4:"data";s:13:"WebDevStudios";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:8:"WordCamp";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:9:"San Diego";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:32:"http://webdevstudios.com/?p=6172";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:380:"We are giving everyone a chance to WIN our tickets!  For a chance to win one of our tickets to WordCamp San Diego all you have to do is visit our Facebook page, Like us and leave a post telling us why you want to attend WordCamp San Diego. <a href="http://webdevstudios.com/2011/06/17/win-a-ticket-to-wordcamp-san-diego/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"April Heline";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1472:"<p>WebDevStudios is proud to announce that we are sponsoring <a title="WordCamp San Diego" href="http://2011.sandiego.wordcamp.org/">WordCamp San Diego</a> which is taking place on July 16th, 2011 at San Diego State University.  The event will feature two separate tracks with speakers covering a variety of topics from using WordPress for web development to creating a successful blog.</p>\n<p>If you haven&#8217;t gotten your ticket to the event they are already SOLD OUT.  However, WebDevStudios will not be able to make it to the west coast to attend WordCamp San Diego this year so we are giving everyone a chance to WIN our tickets!  For a chance to win one of our tickets to WordCamp San Diego all you have to do is <a href="http://www.facebook.com/pages/WebDevStudios/142666175787162?ref=ts">visit our Facebook page</a>, Like us and leave a post telling us why you want to attend WordCamp San Diego.  Two people will be chosen on Friday, June 24th and given our tickets to the event!</p>\n<p>There are many people looking for tickets to WordCamp San Diego so we ask that you please enter the drawing <strong>only</strong> if you are planning on attending this year.</p>\n<p>For more information about WordCamp San Diego <a href="http://2011.sandiego.wordcamp.org/">visit the website</a>, <a href="https://twitter.com/#!/WordCampSD">follow them on Twitter</a>, or <a href="https://www.facebook.com/home.php?sk=group_192965014071799">find them on Facebook</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:76:"http://webdevstudios.com/2011/06/17/win-a-ticket-to-wordcamp-san-diego/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:7;a:6:{s:4:"data";s:50:"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n		\n		\n			\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:52:"Billions – A Free WordPress Theme by WebDevStudios";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:85:"http://webdevstudios.com/2011/06/03/billions-a-free-wordpress-theme-by-webdevstudios/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:94:"http://webdevstudios.com/2011/06/03/billions-a-free-wordpress-theme-by-webdevstudios/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 03 Jun 2011 14:34:38 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:4:{i:0;a:5:{s:4:"data";s:7:"General";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:9:"WordPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:6:"themes";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:13:"WebDevStudios";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:32:"http://webdevstudios.com/?p=6030";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:359:"Billions was created collaboratively with TurboTax who contacted us looking to create a free theme focused on financial bloggers. The theme is available for download in the wordpress.org theme repository. <a href="http://webdevstudios.com/2011/06/03/billions-a-free-wordpress-theme-by-webdevstudios/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:13:"Chris Cochran";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:5871:"<p><a href="http://webdevstudios.com/wp-content/uploads/2011/06/billions-homepage.jpg" rel="shadowbox[sbpost-6030];player=img;"><img class="alignleft size-medium wp-image-6031" title="billions-homepage" src="http://webdevstudios.com/wp-content/uploads/2011/06/billions-homepage-300x257.jpg" alt="" width="300" height="257" /></a>Last week WebDevStudios pushed their first theme to the WordPress theme repository called &#8220;<a title="Billions" href="http://wordpress.org/extend/themes/billions">Billions</a>.&#8221;</p>\n<p>Billions was created collaboratively with TurboTax who contacted us looking to create a free theme focused on financial bloggers and would be available for download in the <a title="Free Financial WordPress Theme" href="http://wordpress.org/extend/themes/billions">WordPress.org</a> theme repository.</p>\n<p><a title="Billions" href="http://wordpress.org/extend/themes/billions"></a></p>\n<p>Some key features we included are advanced SEO options, sharing options, flexibility, and of course a great design. Working closely with the team at Intuit&#8217;s TurboTax, we crafted the theme to their specifications as well WordPress standards including:</p>\n<ul class="alignleft">\n<li><strong>Advance SEO Options</strong>\n<p class="description"><em>Get the edge in search ranking over your competitors with advanced search engine optimization features control out of the box.  Easily set custom title, meta description and keyword tags on posts, pages, categories, and your home page.  You can also include additional custom meta tags as needed. </em></p>\n</li>\n<li><strong>Integrated Breadcrumb Trails</strong>\n<p class="description"><em>Billions integrates the popular breadcrumb plugin by Yoast to give easy breadcrumb control and page trails for your content.</em></p>\n</li>\n<li><strong>Facebook, Twitter and RSS link options.</strong>\n<p class="description"><em> </em></p>\n<p><em> </em></p>\n<p><em> </em></p>\n<p><em> </em></p>\n<p><em> </em></p>\n<p><em> </em></p>\n<p><em> </em></p>\n<p><em></p>\n<div id="attachment_6067" class="wp-caption alignleft" style="width: 325px"><img class="size-full wp-image-6067 " title="social-links-box" src="http://webdevstudios.com/wp-content/uploads/2011/06/social-links-box.jpg" alt="Social Link box" width="315" height="81" /><p class="wp-caption-text">Get social and add your links to your social network profiles.</p></div>\n<p></em><em> </em><em> </em><em> </em><em> </em><em> </em><em> </em><em>Let your readers know where to find you on Twitter or Facebook by adding your facebook profile link and twitter user name in the theme options.</em></li>\n<p><strong> </strong><br />\n<strong> </strong></p>\n<li><strong>In Post Sharing links</strong>\n<div id="attachment_6061" class="wp-caption alignright" style="width: 154px"><img class="size-full wp-image-6061 " title="sharebox" src="http://webdevstudios.com/wp-content/uploads/2011/06/sharebox.jpg" alt="Social Share Box" width="144" height="283" /><p class="wp-caption-text">The sharebox allows readers to easily share your article.</p></div>\n<p><em>Get your content out onto the web in more places by letting your reader share it. The post share box has easy share links for twitter, digg, stumbleupon, facebook, reddit and google buzz to let anyone share your post on their favorite networks.</em></li>\n<li><strong>Custom Header Support</strong>\n<p class="description"><em>With the release of WordPress 3.0 came custom header support for themes. Feel free to swap out the pre-uploaded header with an image of your own.</em></p>\n</li>\n<li><strong>Custom Background Support</strong>\n<p class="description"><em>WordPress 3.0 also introduced support for custom background. Supported in the billions theme this allows the user to upload a custom image or a color to be used as their sites background.</em></p>\n</li>\n<li><strong>YARPP support</strong>\n<p class="description"><em>Yet Another Related Posts Plugin (YARPP) gives you a list of posts and/or pages related to the current entry, introducing the reader to other relevant content on your site. Simply download the plugin and start seeing related content below each post! Select our custom thumbnail template for an added bonus.</em></p>\n</li>\n<li><strong>SiteMap</strong>\n<p class="description"><em>Create a page and chose the sitemap template to give your readers and search engines a quick access page to all your content.</em></p>\n</li>\n</ul>\n<div id="attachment_6078" class="wp-caption alignright" style="width: 172px"><img class="size-full wp-image-6078 " title="makeityours" src="http://webdevstudios.com/wp-content/uploads/2011/06/makeityours.jpg" alt="Billions Options" width="162" height="214" /><p class="wp-caption-text">Take advantage of the built in options to make the billions yours.</p></div>\n<p><strong>Additional Features:</strong></p>\n<li>Simple Image Gallery Display</li>\n<li>Author Archives</li>\n<li>7 Registered Widget Areas (including a widgetized footer).</li>\n<li>3 Menu Locations</li>\n<li>Custom Hompage</li>\n<li>Threaded Comments</li>\n<li>Featured Image Support</li>\n<li>Blog Template</li>\n<li>Custom Tag and Category Archives</li>\n<p>&nbsp;</p>\n<p>Building a theme for the WordPress Theme Repository was a great learning experience and I recommend it for any theme developer.  Make sure to check back for &#8220;Making a WordPress Theme for the WordPress.org Theme Directory&#8221; which will go in depth on exactly what is needed to get your theme into the Free Theme Directory, as well as some tips learned through the process of submitting Billions.</p>\n<blockquote><p>Billions is a new WordPress theme featuring advanced SEO options, integrated breadcrumb trails, and easy sharing links to help promote your content as well as much more. <a href="http://wordpress.org/extend/themes/billions">Download it now for FREE from the WordPress Theme Repository</a>.</p></blockquote>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:90:"http://webdevstudios.com/2011/06/03/billions-a-free-wordpress-theme-by-webdevstudios/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:8;a:6:{s:4:"data";s:53:"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n			\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:33:"Busy Tech Week for WebDevStudios!";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:69:"http://webdevstudios.com/2011/05/18/busy-tech-week-for-webdevstudios/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:78:"http://webdevstudios.com/2011/05/18/busy-tech-week-for-webdevstudios/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 18 May 2011 20:42:22 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:5:{i:0;a:5:{s:4:"data";s:7:"General";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:13:"WebDevStudios";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:8:"WordCamp";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:14:"BlogWorld Expo";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:9:"WordPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:32:"http://webdevstudios.com/?p=5991";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:417:"The upcoming week is going to be very busy for everyone at WebDevStudios! Friday we are renting a van and driving almost the entire company down to WordCamp Raleigh. We are taking a quick one day breather and then heading up to New York City for BlogWorld &#038; New Media Expo! <a href="http://webdevstudios.com/2011/05/18/busy-tech-week-for-webdevstudios/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"April Heline";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:3429:"<p>The upcoming week is going to be very busy for everyone at WebDevStudios!</p>\n<h2>WordCamp Raleigh</h2>\n<p><img class="alignleft size-full wp-image-5721" title="wordcamp-raleigh-2011" src="http://webdevstudios.com/wp-content/uploads/2011/02/wordcamp-raleigh-2011.jpg" alt="" width="125" height="125" /></p>\n<p>Friday we are renting a van and driving almost the entire company down to <a title="WordCamp Raleigh" href="http://wordcampraleigh.com/">WordCamp Raleigh</a>.  This year Brad will be speaking on &#8220;Surviving the Zombie Apocalypse Using Custom Post Types and Taxonomies.&#8221;  As always there will be a number of zombie references mixed in with a number of great WordPress tips and tricks.  Anyone who has ever attended one of Brad&#8217;s lectures knows they are not to be missed!</p>\n<p>There will also be a number of great speakers at WordCamp Raleigh including our very talented friends <a href="http://twitter.com/#!/ryancduff">Ryan Duff</a>, <a href="http://twitter.com/#!/syedbalkhi">Syed Balkhi</a>, <a href="http://twitter.com/#!/OneFineJay">Jayvie Canono</a>, <a href="http://twitter.com/#!/iFroggy">Patrick O’Keefe</a> and many many more.</p>\n<p>This year WordCamp Raleigh is a two-day event being held on May 21st and 22nd.  The conference will once again be held at the beautiful <a title="Sheraton Raleigh Hotel" href="http://www.starwoodhotels.com/sheraton/property/overview/index.html?propertyID=434&amp;EM=VTY_SI_434_RALEIGH_PROP_OVERVIEW">Sheraton Raleigh Hotel</a>.</p>\n<p>If you would like to attend WordCamp tickets are still available <a href="http://wordcampraleigh.com/register/">here</a>!</p>\n<p>Stay up-to-date with the going ons at WordCamp Raleigh via <a title="WordCamp Raleigh" href="http://wordcampraleigh.com">the website,</a> <a title="WordCamp Raleigh" href="http://twitter.com/#!/wordcampraleigh">Twitter</a> and <a title="WordCamp Raleigh" href="http://www.facebook.com/WordCampRaleigh">Facebook</a>.</p>\n<h2>BlogWorld &amp; New Media Expo</h2>\n<p><a rel="attachment wp-att-5875" href="http://webdevstudios.com/events/blogworld-expo-2011/blogworld11_nyla_125x125/"><img class="alignleft size-full wp-image-5875" title="BlogWorld11_NYLA_125x125" src="http://webdevstudios.com/wp-content/uploads/2011/03/BlogWorld11_NYLA_125x125.gif" alt="" width="125" height="125" /></a>Right after WordCamp Raleigh, we are taking a quick one day breather and then heading up to New York City for <a title="BlogWorld Expo" href="http://www.blogworldexpo.com/">BlogWorld &amp; New Media Expo</a>!</p>\n<p>This year the people at BlogWorld have decided to split their awesome conference into two with an East Coast conference in New York City and a West Coast conference in Los Angeles.  Next week we will be attending the first conference at the Jacob Javits Center in New York City and we even have a booth in the expo hall!  If you are attending BlogWorld Expo be sure to visit Booth #502 for chances to win great prizes, find out some information on our services or just talk WordPress shop.</p>\n<p>Tickets for the conference <em>and </em>virtual tickets are still available <a href="http://www.blogworldexpo.com/2011-nyc/registration-travel/register-to-attend-2/">here</a>.  Information about BlogWorld Expo is also available on <a title="BlogWorld Expo" href="http://www.blogworldexpo.com">the website</a> and on <a href="http://twitter.com/#!/blogworldexpo">Twitter</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:74:"http://webdevstudios.com/2011/05/18/busy-tech-week-for-webdevstudios/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:9;a:6:{s:4:"data";s:50:"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n		\n		\n			\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:70:"Operation Homefront’s New BuddyPress Site Connects Military Families";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:104:"http://webdevstudios.com/2011/05/13/operation-homefronts-new-buddypress-site-connects-military-families/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:113:"http://webdevstudios.com/2011/05/13/operation-homefronts-new-buddypress-site-connects-military-families/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 13 May 2011 14:30:18 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:4:{i:0;a:5:{s:4:"data";s:7:"bbPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:10:"BuddyPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:15:"Client Websites";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:9:"WordPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:32:"http://webdevstudios.com/?p=5957";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:384:"Operation Homefront now has a wonderful BuddyPress community site that is the fun and uplifting place that the organization envisioned and where members can share the ups and downs of life as a military family. <a href="http://webdevstudios.com/2011/05/13/operation-homefronts-new-buddypress-site-connects-military-families/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"April Heline";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:2406:"<p><a title="Operation Homefront" href="http://www.operationhomefront.net">Operation Homefront</a> is an amazing organization that provides emergency financial and other assistance to the families of our service members and wounded warriors.  Through generous, widespread public support and a collaborative team of exceptional staff and volunteers, they aspire to become the provider of choice for emergency financial and other assistance to the families of our service members and wounded warriors.</p>\n<p>We met Operation Homefront at BlogWorld Expo 2010 and hit it off right away.  They were looking for developers to migrate their existing forum into a new friendly easy to use system and to help them create a special community site dedicated to military families that would put people in touch with people that were having the same experiences and hardships.  After hearing about the kind of community they were looking for, we knew that <a title="BuddyPress Design and Development for WordPress" href="http://webdevstudios.com/buddypress/">BuddyPress</a> would be perfect!</p>\n<p><a href="http://webdevstudios.com/wp-content/uploads/2011/05/Home1.jpg" rel="shadowbox[sbpost-5957];player=img;"><img class="alignleft size-full wp-image-5959" title="Home" src="http://webdevstudios.com/wp-content/uploads/2011/05/Home1.jpg" alt="" width="800" height="600" /></a></p>\n<p>The new <a title="Operation Homefront Community" href="http://community.operationhomefront.net/">community BuddyPress</a> site allows military families to connect with people who have shared interests, common concerns or are just someone to interact with. You can join a group or create your own. You can blog about or link to things that interest you. You can share photos or view the album of a friend.  It is a great site to meet people who have family in the military and share your story.</p>\n<p>Operation Homefront now has a wonderful BuddyPress community site that is the fun and uplifting place that the organization envisioned and where members can share the ups and downs of life as a military family member.  Be sure to visit the new <a title="Operation Homefront Community!" href="http://community.operationhomefront.net/">Operation Homefront Community!</a> and visit our <a href="http://webdevstudios.com/work-portfolio/operation-homefront-community/">Operation Homefront work portfolio</a> for more details.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:109:"http://webdevstudios.com/2011/05/13/operation-homefronts-new-buddypress-site-connects-military-families/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:2:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";s:4:"href";s:41:"http://feeds.feedburner.com/webdevstudios";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"rel";s:3:"hub";s:4:"href";s:32:"http://pubsubhubbub.appspot.com/";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:42:"http://rssnamespace.org/feedburner/ext/1.0";a:3:{s:4:"info";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:1:{s:3:"uri";s:13:"webdevstudios";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:14:"emailServiceId";a:1:{i:0;a:5:{s:4:"data";s:13:"webdevstudios";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:18:"feedburnerHostname";a:1:{i:0;a:5:{s:4:"data";s:28:"http://feedburner.google.com";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:9:{s:12:"content-type";s:23:"text/xml; charset=UTF-8";s:4:"etag";s:27:"dYiezKSBv+QA5KB2v16a28vA5To";s:13:"last-modified";s:29:"Tue, 13 Mar 2012 07:58:06 GMT";s:4:"date";s:29:"Tue, 13 Mar 2012 09:03:53 GMT";s:7:"expires";s:29:"Tue, 13 Mar 2012 09:03:53 GMT";s:13:"cache-control";s:18:"private, max-age=0";s:22:"x-content-type-options";s:7:"no