ex";s:0:"";s:21:"ex_field3_regex_error";s:0:"";s:15:"ex_field3_notes";s:0:"";s:21:"ex_field3_notes_after";s:0:"";s:17:"ex_field4_default";s:1:"0";s:22:"ex_field4_default_text";s:0:"";s:13:"ex_field4_req";s:5:"false";s:15:"ex_field4_label";s:0:"";s:14:"ex_field4_type";s:4:"text";s:17:"ex_field4_max_len";s:0:"";s:19:"ex_field4_label_css";s:0:"";s:19:"ex_field4_input_css";s:0:"";s:20:"ex_field4_attributes";s:0:"";s:15:"ex_field4_regex";s:0:"";s:21:"ex_field4_regex_error";s:0:"";s:15:"ex_field4_notes";s:0:"";s:21:"ex_field4_notes_after";s:0:"";}', 'yes'),
